draw(0.85*P--(0.85*P + 0.15*(V - P))--(P + 0.15*(V - P)));
draw(O--V,Arrow3(6));
draw(P--W,Arrow3(6));
draw(O--P,Arrow3(6));
draw(O--R,Arrow3(6));
